import {useState,useEffect} from 'react';
import {useDispatch,useSelector} from 'react-redux'
import { Container, Row, Col ,Button,Alert, Spinner}from 'react-bootstrap';
import './App.css';
import {AddForm} from './components/form/Addform';
import {TaskLists} from './components/taskList/TaskLists';
import {NotToDoLists} from './components/notToDoLists/NotToDoLists';
import {TotalHr} from './components/totalHour/TotalHr';
import { HourSaved } from './components/hourSaved/HourSaved';
import {createTask, getTaskLists,switchTask,deleteTaskLists} from './api/taskApi.js';

// from taskAction js 

import {addTask} from './components/taskList/taskAction'

const  App = () => {


const {isPending} = useSelector(state => state.task)

// local state
const [taskList, setTaskList] = useState([]);
const [notToDoList, setNotToDoList] = useState([])
const [index, setIndex] = useState([])
const [noIndex, setNoIndex] = useState([])

const [response, setResponse] = useState({
  message:"",
  status: ""
})

// const [isPending, setIsPending] = useState(false)

useEffect(() => {

  const initialTask = async () => {
    const fetchTask = await getTaskLists();
    const toData = fetchTask.filter((noList) => noList.todo)
    const notTOdata = fetchTask.filter((noList) => !noList.todo)
      fetchTask && fetchTask.length && setTaskList(toData)
      notTOdata && notTOdata.length && setNotToDoList(notTOdata)


  }
  initialTask();


},[])

const getAllTask = async () => {

  const fetchTask = await getTaskLists() || [];

  const toData = fetchTask.filter((noList) => noList.todo)

  const notTOdata = fetchTask.filter((noList) => !noList.todo)

    setTaskList(toData)
    setNotToDoList(notTOdata)

}

const handleOnAddTask =  async formData => {

  if ((Number(formData.hr) + totalHour) > 168 ) {

    alert("You are exhausting yourself, Your have exceeded more than 168 hrs ")

  } else {

    // setIsPending(true)
    const res = await createTask(formData);
    setResponse(res)
    // setIsPending(false)

    res.status === "success" && getAllTask();
}
}

const handleOnRemoveTask = index => {

  const item = taskList[index];

  const newArray = taskList.filter((item, i) => i !== index);
  setTaskList(newArray)

  setNotToDoList([...notToDoList, item]);

  console.log(notToDoList)
}

const handleOnDeleteTask = () => {



  if(window.confirm("Are you sure want to delete the selected items? ")){
    const totalIndex = index.concat(noIndex)

    console.log("Jodeko Index >>>", totalIndex)

    const updatedArray = taskList.filter((item,i) => !index.includes(i));
    const updatedNotArray = notToDoList.filter((item,i) => !index.includes(i));
    console.log("Updated index after delete>>", updatedArray)
    console.log("Updated no index after delete>>", updatedNotArray)
    setTaskList(updatedArray)
    setNotToDoList(updatedNotArray)
    setIndex([]);
    setNoIndex([]);
  } 
}



const markAsToDo = index => {

  const item = notToDoList[index];
  const newArray = notToDoList.filter((item, i) => i !== index);

  setNotToDoList(newArray);

  setTaskList([...taskList, item]);
}

const calcTotalHours = list => {
  let total = 0 ;
  for (let i= 0; i < list.length ; i++){
    total = Number(list[i].hr) + total ;

  }
  return total ;
}

const hourSaved =  calcTotalHours(notToDoList);
const hoursAllocated = calcTotalHours(taskList);
const totalHour = hourSaved + hoursAllocated ;


  return (
          <div className="App">
                <Container>
            <Row>
              <Col>
              <div className="text-center mt-5" > <h1> NOT TO DO LIST </h1></div>
              </Col>
            </Row>
            <hr />
            <div>
              {response.message && 
              <Alert variant={response.status === 'success' ? "success" : "danger"}> {response.message}</Alert>
              }
              { isPending && 
              <Spinner animation="border"></Spinner>
            }
            </div>
            <AddForm handleOnAddTask={handleOnAddTask}/>
            <hr/>
            <Row>
              <Col>
              <TaskLists 
              taskList={taskList}
              handleOnRemoveTask={handleOnRemoveTask}
              index={index}
              setIndex={setIndex}
              />
              </Col>
              <Col>
              <NotToDoLists 
              notToDoList={notToDoList}
              markAsToDo={markAsToDo}
              noIndex={noIndex}
              setNoIndex={setNoIndex}
              />
              </Col>
            </Row>
            <Row>
              <Col>
              <Button variant="danger" onClick={() => handleOnDeleteTask(index)}> Delete </Button>
              </Col>
            </Row>
            <Row>
              <Col>
              <TotalHr totalHour={totalHour}/>
              </Col>
              <Col>
              <HourSaved hourSaved={hourSaved}/>
              </Col>
            </Row>
          </Container>

          </div>
  );
}


export default App ;
