import {requestPending,addTaskSuccess,requestFail, getTaskLists} from './taskSlice.js'
import {createTask} from '../../api/taskApi.js'

export const addTask = formData => async dispatch => {
    try {
        dispatch(requestPending());
        const result = await createTask(formData);
        dispatch(addTaskSuccess(result))

        // result.status === "success" && getAllTask();

    } catch (error) {
        dispatch(requestFail(error.message))
    }
}

const fetchTaskLists = () => async dispatch => {
    try {
        dispatch(requestPending());
        const fetchTask = await getTaskLists() || [];

    } catch (error) {
        dispatch(requestFail(error.message))

    }
}
