import React, {useState} from 'react'
import {Table, Button} from 'react-bootstrap'



export const TaskLists = ({taskLists,handleOnRemoveTask,handleOnDeleteTask}) => {

  const [index, setIndex] = useState([])

  const ifChecked = (i)=> {
    if(index.includes(i)){
      setIndex([...index, ])
    } else {
      index.pop(i)
    }

    console.log("index>>>", index)

  }


    return (
        <>
        <h2>Task list</h2>
        <Table striped bordered hover size="sm">
  <thead>
    <tr>
      <th></th>
      <th>Task</th>
      <th>Hours</th>
      <th>Action</th>
    </tr>
  </thead>
  <tbody>

        {taskLists?.map((t,index) => {

            return (
                <tr key={index}>
                <td><input onChange={()=> ifChecked(index) }type="checkbox" /></td>
                <td>{t.title}</td>
                <td>{t.hr}</td>
                <td>
                  <Button onClick={() => handleOnRemoveTask(index)}> Mark As Not To </Button>
                  </td>
                </tr>
            )

            })
        }
        <tr>
          <td><Button variant="danger" style={{marginLeft:'5px'}}onClick={() => handleOnDeleteTask()}> Delete </Button></td>
        </tr>


  </tbody>
</Table>
</>
    )
}
