import React from 'react'
import {Table, Button} from 'react-bootstrap'



export const TaskLists = ({taskList,handleOnRemoveTask,index,setIndex}) => {

  console.log("From Tasklist Component >>>", taskList)

  const ifChecked = e => {
    const {checked, value} = e.target;
    console.log(checked, value)
    if(checked){
      return setIndex([...index, value])

    } 
      const removeIndex = index.filter((item) => item !== value);
      setIndex(removeIndex)
      console.log("index after popping>>", index)

  }

    return (
          <>
          <h2>Task list</h2>
          <Table striped bordered hover size="sm">
            <thead>
              <tr>
                <th>Task</th>
                <th>Hours</th>
                <th>Action</th>
              </tr>
            </thead>
          <tbody>

                {taskList?.map((t,index) => {

                    return (
                        <tr key={t._id}>
                        <td>
                        <input onChange={ifChecked}  
                        defaultValue=
        {t._id} type="checkbox" />
                        {""}
                        <label>{t.title}</label></td>
                        <td>{t.hr}</td>
                        <td>
                          <Button onClick={() => handleOnRemoveTask(t._id)}> Mark As Not To </Button>
                          </td>
                        </tr>
                    )

                    })
                }

          </tbody>
        </Table>
  </>
    )
}
